import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

function ListeArtisans() {
  const [artisans, setArtisans] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const nom = queryParams.get('nom');

    const fetchArtisans = async () => {
      try {
        const url = nom
          ? `http://localhost:3001/api/artisans/search?nom=${encodeURIComponent(nom)}`
          : `http://localhost:3001/api/artisans`;

        const response = await axios.get(url);
        setArtisans(response.data);
      } catch (err) {
        console.error('Erreur lors de la récupération des artisans:', err);
        setArtisans([]);
      } finally {
        setLoading(false);
      }
    };

    fetchArtisans();
  }, [location.search]);

  if (loading) return <p>Chargement...</p>;

  return (
    <div>
      <h2>Liste des artisans</h2>
      {artisans.length === 0 ? (
        <p>Aucun artisan trouvé</p>
      ) : (
        <ul>
          {artisans.map(artisan => (
            <li key={artisan.id}>
              {artisan.nom} ({artisan.specialite?.nom || 'Spécialité inconnue'})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ListeArtisans;
