import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios'; // Assure-toi que tu as axios installé

const ListeArtisans = () => {
  const [artisans, setArtisans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const categorie = params.get('categorie');

  useEffect(() => {
    const fetchArtisans = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:5000/api/artisans/categorie/${categorie}`);
        setArtisans(response.data);
      } catch (err) {
        setError('Erreur lors de la récupération des artisans');
      } finally {
        setLoading(false);
      }
    };

    fetchArtisans();
  }, [categorie]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Artisans dans la catégorie: {categorie}</h2>
      {artisans.length > 0 ? (
        <ul>
          {artisans.map((artisan) => (
            <li key={artisan.id}>
              <h3>{artisan.nom}</h3>
              <p>{artisan.specialite ? artisan.specialite.nom : 'Pas de spécialité'}</p>
              <p>{artisan.ville}</p>
              <p>{artisan.a_propos}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>Aucun artisan trouvé dans cette catégorie.</p>
      )}
    </div>
  );
};

export default ListeArtisans;
