// Import des hooks et bibliothèques nécessaires
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import '../scss/ListeArtisans.scss';
import { Link } from 'react-router-dom';

// Composant principal
function ListeArtisans() {
  const [artisans, setArtisans] = useState([]); // Stocke les artisans récupérés
  const [loading, setLoading] = useState(true); // Gère l'affichage du chargement
  const location = useLocation(); // Permet d'accéder aux paramètres de l'URL

  // Effet déclenché à chaque changement d'URL (recherche ou catégorie)
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search); // Extraction des paramètres de requête
    const nom = queryParams.get('nom'); // Recherche par nom
    const categorie = queryParams.get('categorie'); // Filtrage par catégorie

    // Fonction de récupération des artisans via l'API
    const fetchArtisans = async () => {
      try {
        let url = 'http://localhost:3001/api/artisans';

        // Si un nom est spécifié, on cherche par nom
        if (nom) {
          url += `/search?nom=${encodeURIComponent(nom)}`;
        } 
        // Sinon, si une catégorie est spécifiée
        else if (categorie) {
          url += `/categorie/${encodeURIComponent(categorie)}`;
        }

        const response = await axios.get(url); // Appel API
        setArtisans(response.data); // Mise à jour du state
      } catch (err) {
        console.error('Erreur lors de la récupération des artisans:', err);
        setArtisans([]); // Réinitialise la liste en cas d’erreur
      } finally {
        setLoading(false); // Fin du chargement
      }
    };

    fetchArtisans();
  }, [location.search]); // Re-lance l’effet si l’URL change

  // Fonction pour afficher les étoiles de notation
  const renderStars = (note) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={i <= note ? 'star filled' : 'star'}>★</span>
      );
    }
    return stars;
  };

  // Affichage pendant le chargement
  if (loading) return <p className="loading">Chargement...</p>;

  const getTitreCategorie = () => {
    if (categorie) {
      switch (categorie) {
        case 'batiment':
          return 'Artisans du Bâtiment';
        case 'services':
          return 'Artisans de Services';
        case 'fabrication':
          return 'Artisans de Fabrication';
        case 'alimentation':
          return 'Artisans de l’Alimentation';
        default:
          return 'Liste des Artisans';
      }
    } else if (nom) {
      return `Résultats pour "${nom}"`;
    } else {
      return 'Tous les Artisans';
    }
  };


  // Rendu principal
  return (
    <div className="artisans-container">
      <h2 className="title">{getTitreCategorie()}</h2>


      {artisans.length === 0 ? (
        <p className="no-result">Aucun artisan trouvé</p> // Si aucun artisan trouvé
      ) : (
        <div className="card-grid">
          {artisans.map((artisan) => (
            <Link to={`/artisan/${artisan.id}`} key={artisan.id} className="artisan-card">
              <h3>{artisan.nom}</h3>
              <p><strong>Spécialité :</strong> {artisan.specialite?.nom || 'Non renseignée'}</p>
              <p><strong>Localisation :</strong> {artisan.ville || 'Non précisée'}</p>
              <p className="stars">{renderStars(artisan.note || 0)}</p>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
}

export default ListeArtisans;
