import React from 'react';
import { Container, Row, Col, Card, ListGroup } from 'react-bootstrap';

// Composant réutilisable et cliquable
function CarteClickable({ titre, texte, contenu, onClick }) {
  return (
    <Card className="shadow-sm h-100" onClick={onClick} style={{ cursor: 'pointer' }}>
      <Card.Header>{titre}</Card.Header>
      {contenu ? (
        <ListGroup variant="flush">
          {contenu.map((item, idx) => (
            <ListGroup.Item key={idx}>{item}</ListGroup.Item>
          ))}
        </ListGroup>
      ) : (
        <Card.Body>
          <p>{texte}</p>
        </Card.Body>
      )}
    </Card>
  );
}

function Home() {
  // Données 100% personnalisables
  const cardsData = [
    { titre: 'Étape 1', texte: 'Choisissez un artisan', contenu: null },
    { titre: 'Étape 2', texte: 'Expliquez vos besoins', contenu: null },
    { titre: 'Étape 3', texte: 'Recevez des devis', contenu: null },
    { titre: 'Étape 4', texte: 'Évaluez et engagez', contenu: null },
    {
      titre: 'Infos utiles 1',
      contenu: ['Cras justo odio', 'Dapibus ac facilisis in', 'Vestibulum at eros'],
    },
    {
      titre: 'Infos utiles 2',
      contenu: ['Travail garanti', 'Artisans certifiés', 'Service client'],
    },
    {
      titre: 'Infos utiles 3',
      contenu: ['Support 24/7', 'Paiement sécurisé', 'Satisfaction assurée'],
    },
  ];

  // Action quand on clique sur une carte
  const handleClick = (titre) => {
    alert(`Vous avez cliqué sur : ${titre}`);
    // tu peux faire un redirect ici si besoin
  };

  return (
    <Container className="my-5">
      <h1 className="text-center mb-4">Comment trouver mon artisan</h1>
      <h2 className="text-center mb-5">Suivez les étapes ci-dessous</h2>

      <Row className="g-4">
        {cardsData.map((card, index) => (
          <Col key={index} xs={12} sm={6} md={4}>
            <CarteClickable
              titre={card.titre}
              texte={card.texte}
              contenu={card.contenu}
              onClick={() => handleClick(card.titre)}
            />
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Home;
