// AppContent.js ou dans App.js
import React from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import './scss/App.scss';
import Navbar from './components/footer-header/Navbar';
import Footer from './components/footer-header/Footer';
import Home from './pages/Home';
import ListeArtisans from './pages/ListeArtisans';
import FicheArtisan from './pages/FicheArtisan';
import NotFound from './pages/NotFound';

function AppContent() {
  const location = useLocation();
  const isNotFound = location.pathname === '/404' || location.pathname === '*' || location.pathname.startsWith('/some-invalid-path');

  return (
    <>
      {!isNotFound && <Navbar />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/liste-artisans" element={<ListeArtisans />} />
        <Route path="/artisan/:id" element={<FicheArtisan />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      {!isNotFound && <Footer />}
    </>
  );
}

export default AppContent;
